import React from 'react'

const Header = ({ pseudo }) => {
    const formatPseudo = /[aeiouy]/i.test(pseudo) ? `d'${pseudo}` : `de ${pseudo}`
    return (
        <header>
            <h1>La boite à recette de { formatPseudo }</h1>
        </header>
    )

}

export default Header
